import React from "react"
import { useState, useRef , useEffect} from 'react'
import TodoList from "./TodoList.js";

const LOCAL_STORAGE_KEY = "key"

function App() {

    const [todos, setTodos] = React.useState([])
    const todoNameRef = useRef()

    useEffect(() => {
        const existingTodos = localStorage.getItem(LOCAL_STORAGE_KEY)
        if (existingTodos) setTodos(existingTodos)
    }, [])

    useEffect(() => {
        localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(todos))
    }, [todos])

    const addTodo = () =>{
        const task = todoNameRef.current.value
        if(task === '') return
        setTodos(prevTodos => {
            return [...prevTodos, {id : 1, name : task, complete : false}]
        })
        todoNameRef.current.value = null

    }
  return (
    <>
      <h1>My To-Do List</h1>
      <TodoList todos={todos}/>
      <input ref={todoNameRef} type="text" placeholder="Task?"></input>
      <button onClick={addTodo}>Add Task</button>
      <button>Clear Task</button>
      <h5>0 tasks left</h5>
    </>
  );
}

export default App;
